import React from 'react';
import gql from 'graphql-tag';
import { Mutation } from 'react-apollo';

const RelikeThisMovie = gql`
    mutation($title: String!, $username: String!){
        relikeMovie(title: $title, username: $username){
            _id
        }
    }
`;

const RelikeMovie = ({ movie, user }) => {
  return (
      <Mutation mutation={RelikeThisMovie} variables={{title: movie.title, username: user.username}}>
        {(relikeMovie, {loading, error}) => (
            <button onClick={() => relikeMovie()}>Relike this Movie</button>
        )}
      </Mutation>
  )
}
export default RelikeMovie;
