import React from 'react';
import gql from 'graphql-tag';
import { Mutation } from 'react-apollo';

import Error from './utils/Error';
import { CurrentUserQuery } from './auth/User'

const AddMovie = gql`
    mutation($title: String!, $movieDBId: Int!, $posterPath: String!, $username: String!){
        addMovie(title: $title, movieDBId: $movieDBId, posterPath: $posterPath, username: $username){
            title
            posterPath
            movieDBId
        }
    }
`;

const AddToFavorites = ({movie, movieId, user}) => {
  return (
      <Mutation mutation={AddMovie} 
      variables={{
        title: movie.title, 
      movieDBId: movie.id, 
      posterPath: movie.poster_path,
      username: user.username
      }}
      refetchQueries={[{query: CurrentUserQuery}]}>
      {(addMovie, {loading, error}) => {
          const userFavIds = user.favorites.map(fav => fav.movieDBId);
          const alreadyFavorited = userFavIds.includes(movieId);
          console.log(movie.id)
          console.log(alreadyFavorited)
          return(
              <div>
              {alreadyFavorited ? <button>Unfavorite</button> : <button onClick={() => addMovie()}>Add{loading ? "ing" : ''} To Favorites</button>
              }
                {error && <Error err={error.message}/>}
              </div>

          )
      }}
      </Mutation>
  )
}

export default AddToFavorites;
