import React from 'react';
import gql from 'graphql-tag';
import { Mutation } from 'react-apollo';

import Error from './utils/Error';

const AddMovie = gql`
    mutation($title: String!, $movieDBId: String!, $posterPath: String!, $username: String!){
        addMovie(title: $title, movieDBId: $movieDBId, posterPath: $posterPath, username: $username){
            title
            posterPath
            movieDBId
        }
    }
`;

const AddToFavorites = ({movie, user}) => {
  return (
      <Mutation mutation={AddMovie} 
      variables={{
        title: movie.title, 
      movieDBId: movie.id, 
      posterPath: movie.poster_path,
      username: user.username
      }}>
      {(addMovie, {loading, error}) => {
          console.log(user)
          console.log(movie)
          return(
              <div>
                <button onClick={() => addMovie()}>Add{loading ? "ing" : ''} To Favorites</button>
                {error && <Error err={error.message}/>}
              </div>

          )
      }}
      </Mutation>
  )
}

export default AddToFavorites;
