import React from 'react';
import gql from 'graphql-tag';
import { Mutation } from 'react-apollo';

const AddMovie = gql`
    mutation($title: String!, $movieDBId: String!, $posterPath: String!, $username: String!){
        addMovie(title: $title, movieDBId: $movieDBId, posterPath: $posterPath, username: $username){
            title
            posterPath
            movieDBId
        }
    }
`;

const AddToFavorites = ({movie, currentUser}) => {
    const addToFav = () => {
        console.log("movie added")
    }
  return (
      <Mutation mutation={AddMovie} 
      variables={{title: movie.title, 
      movieDBId: movie.id, 
      posterPath: movie.poster_path, 
      username: currentUser.username
      }}>
      {(addmovie, {loading, error}) => {
          return(
            <button onClick={() => addToFav()}>Add To Favorites</button>
          )
      }}
      </Mutation>
  )
}

export default AddToFavorites;
