import React, { Component } from 'react';

class Movies extends Component {
    state = {
        movies: null
    }
    async componentDidMount() {
        try {
          const res = await fetch(`https://api.themoviedb.org/3/discover/movie?a8cdfa69af55f80964d61a4a96025998&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1`);

          const movies = await res.json();
          this.setState({
            movies: movies.results
          });

        }catch(e) {
          console.log(e);
        }
      }
    render() {
        console.log(this.state.movies)
        return(
            <div>
                <h1>Movies goes here</h1>
                <ul>{this.state.movies}</ul>
            </div>
        )
    }
}

export default Movies;
