import React, { Component } from 'react';
import User from './auth/User';
import PleaseSignin from './auth/PleaseSignin';
import MoviePoster from './MoviePoster';


class Profile extends Component {
    state = {
        movies: [],
        myMovies: [{name: "Aquaman"}, {name: "Bumblebee"}, {name: "Bird Box"}, {name: "Creed II"}]
    }
    async componentDidMount() {
        try {
          const res = await fetch(`https://api.themoviedb.org/3/discover/movie?api_key=a8cdfa69af55f80964d61a4a96025998&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${this.state.page}`);

          const movies = await res.json();
          this.setState({
            movies: movies.results
          });

        }catch(e) {
          console.log(e);
        }
      }
  render() {
      const {movies, myMovies } = this.state;
      const userMovie = myMovies.map(usermovie =>(
          usermovie.name
      ));
      var userMovies = movies.filter(movie => {
          const title = movie.title
          return userMovie.indexOf(title) > -1;
      });

      console.log(userMovies)
    return (
    <PleaseSignin>
            <User>
            {({data: {currentUser}, loading}) => {
                console.log(currentUser)
                if(loading) return <p>Loading...</p>;
                return(
                <div>
                    <h1>{currentUser.username}' Profile</h1>
                    <p>{currentUser.email}</p>
                    <ul>
                            {currentUser.favorites.map(fav => (
                            <MoviePoster movie={fav} key={fav.id} />
                            ))}
                    </ul>
                </div>
                )}}
            </User>
    </PleaseSignin>
    )
  }
}
export default Profile;
